    private void bt_loginActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bt_loginActionPerformed
        user u = new user();
    try {
        // Buat koneksi ke database
        java.sql.Connection conn = koneksi.getConnection();

        // Buat query dengan menggunakan parameter untuk mencegah SQL Injection
        String query = "SELECT * FROM tb_pengguna WHERE Username=? AND Password=?";
        this.stat = conn.prepareStatement(query);

        // Set parameter pada query
        this.stat.setString(1, u.Username);
        this.stat.setString(2, u.Password);

        // Eksekusi query
        this.rs = this.stat.executeQuery();

        // Periksa apakah hasil query tidak kosong
        if (rs.next()) {
            // Jika login berhasil, buka frame TipeUser
            TipeUser tu = new TipeUser();
            tu.setVisible(true);
            tu.revalidate();
            dispose();
        } else {
            // Jika login gagal, tampilkan pesan error
            JOptionPane.showMessageDialog(null, "Username atau Password salah");
        }
    } catch (Exception e) {
        // Tampilkan pesan error jika terjadi kesalahan saat eksekusi query
        JOptionPane.showMessageDialog(null, "Terjadi kesalahan: " + e.getMessage());
    }

    }//GEN-LAST:event_bt_loginActionPerformed

