    private void Bersih() {
        t_idproduk.setText("");
        t_namaproduk.setText("");
        t_harga.setText("");
        t_stok.setText("");
        
        btn_simpan.setText("Simpan");
        t_idproduk.setEditable(true);
    }
    
    private void CariData() {
        try {
            st = connection.createStatement();
            rs = st.executeQuery("SELECT * FROM tb_produk WHERE " +
                combo_cari.getSelectedItem().toString() + 
                " LIKE '%" + t_caridata.getText() + "%'");
            
            DefaultTableModel model = new DefaultTableModel();
            model.addColumn("No.");
            model.addColumn("Id_produk");
            model.addColumn("Id_jenis");
            model.addColumn("Id_supplier");
            model.addColumn("Nama_produk");
            model.addColumn("Harga");
            model.addColumn("Stok");
            
            int no = 1;
            
            model.getDataVector().removeAllElements();
            model.fireTableDataChanged();
            model.setRowCount(0);
            
            while (rs.next()) {
               Object[] data = {
                   no ++,
                   rs.getString("Id_produk"),
                   rs.getString("Id_jenis"),
                   rs.getString("Id_supplier"),
                   rs.getString("Nama_produk"),
                   rs.getString("Harga"),
                   rs.getString("Stok")
               };
               model.addRow(data);
               tabel_data.setModel(model);
            }
            
            
        } catch (Exception e) {
        }
    }
    
    private void TampilData() {
        try {
            st = connection.createStatement();
            rs = st.executeQuery("SELECT * FROM tb_produk");
            
            DefaultTableModel model = new DefaultTableModel();
            model.addColumn("No.");
            model.addColumn("Id_produk");
            model.addColumn("Id_jenis");
            model.addColumn("Id_supplier");
            model.addColumn("Nama_produk");
            model.addColumn("Harga");
            model.addColumn("Stok");
            
            int no = 1;
            
            model.getDataVector().removeAllElements();
            model.fireTableDataChanged();
            model.setRowCount(0);
            
            while (rs.next()) {
               Object[] data = {
                   no ++,
                   rs.getString("Id_produk"),
                   rs.getString("Id_jenis"),
                   rs.getString("Id_supplier"),
                   rs.getString("Nama_produk"),
                   rs.getString("Harga"),
                   rs.getString("Stok")
               };
               model.addRow(data);
               tabel_data.setModel(model);
            }
            
            
        } catch (Exception e) {
        }
    }

