    private void btn_simpanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_simpanActionPerformed
        try {
            st = connection.createStatement();
            if (t_idproduk.getText().equals("") || 
                    combo_jenis.getSelectedItem() == null ||
                    combo_supplier.getSelectedItem() == null ||
                    t_namaproduk.getText().equals("") || 
                    t_harga.getText().equals("") || 
                    t_stok.getText().equals("")) {
                JOptionPane.showMessageDialog(null, "Data tidak boleh kosong", "Validasi Data", JOptionPane.INFORMATION_MESSAGE);
                return;
            }
            //aksi simpan data
            if (btn_simpan.getText() == "Simpan") {
                String cek ="SELECT * FROM tb_produk WHERE Id_produk = '" + t_idproduk.getText() + "'";
                rs = st.executeQuery(cek);
                if (rs.next()) {
                    JOptionPane.showMessageDialog(null, "Maaf..Id_produk ini sudah ada");
                } else {
                    String sql = "INSERT INTO tb_produk VALUES ('" + t_idproduk.getText() + 
                            "','" + combo_jenis.getSelectedItem() +
                            "','" + combo_supplier.getSelectedItem() +      
                            "','" + t_namaproduk.getText() +
                            "','" + t_harga.getText() +
                            "','" + t_stok.getText() + "')";
                    st.executeUpdate(sql);
                    JOptionPane.showMessageDialog(null, "Data berhasil disimpan");
                    Bersih();
                    TampilData();
                }
            }else {
                //aksi ubah data
                    String update = "UPDATE tb_produk SET Nama_produk = '" + t_namaproduk.getText() +
                            "', Harga = '" + t_harga.getText() +
                            "', Stok = '" + t_stok.getText() +       
                            "' WHERE Id_produk = '" + t_idproduk.getText() + "'";
                    st.executeLargeUpdate(update);
                    JOptionPane.showMessageDialog(null, "Data berhasil diubah");
                    Bersih();
                    TampilData();
                    }
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, e);
        }
    }//GEN-LAST:event_btn_simpanActionPerformed

    private void tabel_dataMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tabel_dataMouseClicked
        t_idproduk.setText(tabel_data.getValueAt(tabel_data.getSelectedRow(), 1).toString());
        t_namaproduk.setText(tabel_data.getValueAt(tabel_data.getSelectedRow(), 4).toString());
        t_harga.setText(tabel_data.getValueAt(tabel_data.getSelectedRow(), 5).toString());
        t_stok.setText(tabel_data.getValueAt(tabel_data.getSelectedRow(), 6).toString());
        
        t_idproduk.setEditable(false);
        btn_simpan.setText("Ubah");
                
    }//GEN-LAST:event_tabel_dataMouseClicked

    private void btn_hapusActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_hapusActionPerformed
        if (t_idproduk.getText().equals("")) {
            JOptionPane.showMessageDialog(this, "Silahkan pilih data yang akan dihapus"); 
        } else {
            int jawab = JOptionPane.showConfirmDialog(null, "Apakah yakin menghapus data ini?", "Konfirmasi", JOptionPane.YES_NO_OPTION);
            if (jawab == 0) {
                try {
                    st = connection.createStatement();
                    String sql = "DELETE FROM tb_produk WHERE Id_produk = '" + t_idproduk.getText() + "'";
                    st.executeUpdate(sql);
                    JOptionPane.showMessageDialog(null, "Data berhasil dihapus");
                    TampilData();
                    Bersih();
                    
                } catch (Exception e) {
                     JOptionPane.showMessageDialog(null, e);
                }
                
            }
        }
    }//GEN-LAST:event_btn_hapusActionPerformed

